import { Slider } from "./Slider";

export const Settings = ({ title, name, description, type, defaultValue }) => {
  return (
    <div className="card card-compact w-28 md:w-40 lg:w-96 lg:card-side bg-base-100 mx-4 txt-neutral-content">
      <div className="card-body text-center">
        <h2 className="text-sm card-title justify-center items-center">{title}</h2>
        <p className="italic text-xs text-start" >{description}</p>
        {type === "bool" ? (
          <>
            <select className="select select-bordered select-sm w-full max-w-xs">
              <option disabled selected>False</option>
              <option>True</option>
            </select>
          </>
        ) : type === "int" || type === "float" ? (
          <Slider defaultValue={defaultValue} name={name} />
        ) : (
          <input type="text" placeholder="Type here" className="input input-bordered input-xs w-full max-w-xs" />
        )
        }
        <div className="card-actions justify-end mt-4">
          <div className="badge badge-outline badge-sm">{name.split(":")[0].toUpperCase()}</div>
        </div>
      </div>
    </div>
  );
};
