import { os } from "@neutralinojs/lib";
import { useEffect, useState } from "react";

export const Slider = ({ min, max, defaultValue, name, type }) => {
  const [currentValue, setCurrentValue] = useState(defaultValue);

  async function getCurrentValue() {
    try {
      const res = await os.execCommand(`hyprctl getoption -j ${name}`);
      const data = JSON.parse(res.stdOut);
      setCurrentValue(type === 'int' ? data.int : data.float);
    } catch (error) {
      console.log(error);
    }
  };

  async function runHyprctl(command) {
    try {
      const res = await os.execCommand(command);
    } catch (error) {
      console.log(error);
    }
  }

  async function onChangeValue(e) {
    setCurrentValue(e.target.value);
    runHyprctl(`hyprctl --batch "keyword ${name} ${e.target.value}"`);
  }

  useEffect(() => {
    getCurrentValue();
  }, []);

  return (
    <>
      <div className="flex flex-row justify-between text-xs px-2">
        <input type="range" min={1} max={10} value={currentValue} className="range range-xs" step="1" onChange={onChangeValue} />
        <span className="ml-2">{currentValue.toString().slice(0, 3)}</span>
      </div>
    </>
  )
}
