import { useEffect, useState } from "react";
import { getHyprValue, setHyprValue } from '../utils/hyprctl.js';


export const Slider = ({ min, max, name, type }) => {
  const [currentValue, setCurrentValue] = useState(getHyprValue(name, type));

  async function onChangeValue(e) {
    await setHyprValue(name, e.target.value, type);
    setCurrentValue(e.target.value);
  }

  useEffect(() => {
    setCurrentValue(getHyprValue(name, type));
  }, [name, type]);

  return (
    <>
      <div className="flex flex-row justify-between text-xs px-2">
        <input type="range" min={1} max={10} value={currentValue} className="range range-xs" step="1" onChange={onChangeValue} />
        <span className="ml-2">{currentValue.toString().slice(0, 3)}</span>
      </div>
    </>
  )
}
